import { useState, useEffect } from "react"
import axios from 'axios';

const Poster = (props) => {
    return (
        <div class="film-container">
            <div class="poster">
                <img src={props.src} alt="film" />
            </div>
            <div class="poster">
                    <img src={props.src} alt="film" />
                </div>
        </div>
    );
}

const InitialScreen = () => {
    const arrayFilms = [
        {src:"/imgs/image.png"},
        {src:"/imgs/image.png"},
        {src:"/imgs/image.png"},
        {src:"/imgs/image.png"}
    ];
    // Componet logic
    const [films, setFilms] = useState([]);

    useEffect(() => {
        const promise = axios.get("https://mock-api.driven.com.br/api/v5/cineflex/movies");
        promise.then((response) => {
            console.log(response.data)
            setFilms(response.data)
        });
    }, []);

    const newArrayFilms = films.map((element) => {
        return element.poster
    });

    // Component UI
    return (
        <div class="main-page">
            <div class="initial-message">
                <h1>Selecione o filme</h1>
            </div>
            <div class="films">
                {arrayFilms.map((element) => <Poster src={element.src}/>)}
            </div>

        </div>
    );
}

export default InitialScreen
