                    <div class="poster">
                        <img src="/imgs/image.png" alt="film" />
                    </div>
                </div>
                <div class="film-container">
                    <div class="poster">
                        <img src="/imgs/image.png" alt="film" />
                    </div>
                    <div class="poster">
                        <img src="/imgs/image.png" alt="film" />
                    </div>
                </div>
            </div>
        </div>);
}

export default InitialScreen
